import React from "react";
import { useFormik } from "formik";
import * as Yup from "yup";
import axios from "axios";

const AddEmployeeForm = () => {
  const formik = useFormik({
    initialValues: {
      firstName: "",
      lastName: "",
      employeeID: "",
      email: "",
      phone: "",
      department: "",
      dateOfJoining: "",
      role: "",
    },
    validationSchema: Yup.object({
      firstName: Yup.string().required("First name is required"),
      lastName: Yup.string().required("Last name is required"),
      employeeID: Yup.string()
        .max(10, "Employee ID must be at most 10 characters")
        .required("Employee ID is required"),
      email: Yup.string().email("Invalid email address").required("Email is required"),
      phone: Yup.string()
        .matches(/^[0-9]{10}$/, "Phone number must be 10 digits")
        .required("Phone number is required"),
      department: Yup.string().required("Department is required"),
      dateOfJoining: Yup.date()
        .max(new Date(), "Date of joining cannot be in the future")
        .required("Date of joining is required"),
      role: Yup.string().required("Role is required"),
    }),
    onSubmit: async (values, { resetForm }) => {
      try {
        const response = await axios.post("http://localhost:5000/api/employees", values);
        alert(response.data.message);
        resetForm();
      } catch (error) {
        alert(error.response?.data?.message || "Something went wrong");
      }
    },
  });

  return (
    <form onSubmit={formik.handleSubmit}>
      <div>
        <label>First Name:</label>
        <input
          type="text"
          name="firstName"
          {...formik.getFieldProps("firstName")}
        />
        {formik.touched.firstName && formik.errors.firstName && (
          <div>{formik.errors.firstName}</div>
        )}
      </div>
      <div>
        <label>Last Name:</label>
        <input
          type="text"
          name="lastName"
          {...formik.getFieldProps("lastName")}
        />
        {formik.touched.lastName && formik.errors.lastName && (
          <div>{formik.errors.lastName}</div>
        )}
      </div>
      <div>
        <label>Employee ID:</label>
        <input
          type="text"
          name="employeeID"
          {...formik.getFieldProps("employeeID")}
        />
        {formik.touched.employeeID && formik.errors.employeeID && (
          <div>{formik.errors.employeeID}</div>
        )}
      </div>
      <div>
        <label>Email:</label>
        <input type="email" name="email" {...formik.getFieldProps("email")} />
        {formik.touched.email && formik.errors.email && (
          <div>{formik.errors.email}</div>
        )}
      </div>
      <div>
        <label>Phone:</label>
        <input type="text" name="phone" {...formik.getFieldProps("phone")} />
        {formik.touched.phone && formik.errors.phone && (
          <div>{formik.errors.phone}</div>
        )}
      </div>
      <div>
        <label>Department:</label>
        <select name="department" {...formik.getFieldProps("department")}>
          <option value="" label="Select department" />
          <option value="HR" label="HR" />
          <option value="Engineering" label="Engineering" />
          <option value="Marketing" label="Marketing" />
        </select>
        {formik.touched.department && formik.errors.department && (
          <div>{formik.errors.department}</div>
        )}
      </div>
      <div>
        <label>Date of Joining:</label>
        <input
          type="date"
          name="dateOfJoining"
          {...formik.getFieldProps("dateOfJoining")}
        />
        {formik.touched.dateOfJoining && formik.errors.dateOfJoining && (
          <div>{formik.errors.dateOfJoining}</div>
        )}
      </div>
      <div>
        <label>Role:</label>
        <input type="text" name="role" {...formik.getFieldProps("role")} />
        {formik.touched.role && formik.errors.role && (
          <div>{formik.errors.role}</div>
        )}
      </div>
      <button type="submit">Submit</button>
      <button type="reset" onClick={formik.handleReset}>
        Reset
      </button>
    </form>
  );
};

export default AddEmployeeForm;
